function yelpInit(e){var t=$(".yelp-wrapper");$.ajax("//primetable.herokuapp.com/api/reviews/"+e).done(function(e){$(t).parent().attr("href",e.url),$(t).find(".yelp-img-wrapper img").attr("src",e.image_url),$(t).find(".yelp-title").text(e.name),$(t).find(".rating").attr("data-rating",e.rating),$(t).find(".yelp-bottom-row p").text(e.review_count+" Reviews"),$(t).removeClass("loading")}).fail(function(e){console.log("err",e)})}yelpInit("prime-table-stockton-2");